for(var basketLink=document.querySelectorAll(".card__btn-buy"),basketModal=document.querySelector(".modal-add-to-basket"),basketClose=document.querySelector(".basket-close"),i=0;i<basketLink.length;i++)basketLink[i].addEventListener("click",function(e){e.preventDefault(),basketModal.classList.add("modal-show")});basketClose.addEventListener("click",function(e){e.preventDefault(),basketModal.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&basketModal.classList.contains("modal-show")&&(e.preventDefault(),basketModal.classList.remove("modal-show"))});